import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './PageHeader.css';
import { faCalendarMinus } from '@fortawesome/free-regular-svg-icons';

function MobileHeaderNavigation() {
    const container = useRef(null);
    const [isOpen, setIsOpen] = useState(false);
    useEffect(() => {
        container.current.classList.toggle('closed', !isOpen);
    },[isOpen]);

    return (
        <div className='top-navigation-mobile'>
            <button type='button' onClick={()=> setIsOpen(!isOpen)} aria-expanded={isOpen} aria-controls='mobile-nav'>
                <FontAwesomeIcon icon={["fal", "coffee"]} />
            </button>
            <div id='mobile-nav' className='dropdown-menu' ref={container}>
                <Link to='/'>Home</Link>
                <Link to='/whowehelp'>Who we help</Link>
                <Link to='/whatweoffer'>What we offer</Link>
                <Link to='/whoweare'>Who we are</Link>
                <Link to='/contactus'>Contact us</Link>
            </div>
        </div>
    );
}

export default MobileHeaderNavigation;
