import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './PageHeader.css';
import NavLinks from './NavLinks';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';

function MobileHeaderNavigation() {
    const container = useRef(null);
    const [isOpen, setIsOpen] = useState(false);
    useEffect(() => {
        container.current.classList.toggle('closed', !isOpen);
    },[isOpen]);

    const handleClick = () => {
        console.log('Button was clicked in the child component!');
    }

    return (
        <div className='top-navigation-mobile'>
            <button type='button' onClick={()=> setIsOpen(!isOpen)} aria-expanded={isOpen} aria-controls='mobile-nav'>
                <FontAwesomeIcon icon={faBars} />
            </button>
            <div id='mobile-nav' className='dropdown-menu' ref={container}>
                <NavLinks closeMenu={()=> setIsOpen(!isOpen)} />
            </div>
        </div>
    );
}

export default MobileHeaderNavigation;
