import React, { useState, useEffect, useRef } from 'react';
import './ExpandableSection.css';

function ExpandableSection({heading, subheading, children}) {
  const container = useRef(null);
  const [isOpen, setIsOpen] = useState(false)
  useEffect(() => {
    container.classList.toggle('closed', isOpen);
    // document.getElementsByClassName('expandable-container').classList.toggle('closed', isOpen);
  },[isOpen])
    
  return (
      <div className='expandable-container closed' ref={container}>
        <h2>{heading}</h2>
        <div className='expandable-header'>
          <button type='button' onClick={()=> setIsOpen(!isOpen)}>
            <span>{subheading}</span>
            <span className='closed-expander'>+</span>
            <span className='open-expander'>-</span>
          </button>
        </div>
        <div className='expandable-contents'>
          <p>
              {children}
          </p>
        </div>
      </div>
  );
}

export default ExpandableSection;
