import React, { useState } from 'react';
import './ExpandableSection.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function ExpandableSection({id, icon, heading, subheading, children}) {
  const [isHovered, setIsHovered] = useState(false);

  function MouseEnter(event) {
    console.log("mouse enter")
    setIsHovered(true);
  }

  function MouseOut(event){
    console.log("mouse out")
    setIsHovered(false);
  }

  return (
    <div className="App">
      <button onMouseEnter={MouseEnter} onMouseOut={MouseOut}>Is hovered? {isHovered}</button>
      { isHovered && <div>Hovered!</div> }
    </div>
  );


  const [isOpen, setIsOpen] = useState(false);

  return (
      <div className={`expandable-container ${isOpen ? '' : 'closed'}`}>
        <FontAwesomeIcon icon={icon} className='icon' />
        <h2>{heading}</h2>
        <button
          className='expandable-header'
          type='button'
          aria-expanded={isOpen}
          aria-controls={id}
          onClick={()=> setIsOpen(!isOpen)}
        >
          <span>{ subheading }</span>
          <span>{ isOpen ? '-' : '+' }</span>
        </button>
        {
          <div id={id} className='expandable-contents'>
            <p>
                {children}
            </p>
          </div>
        }
      </div>
  );
}

export default ExpandableSection;
