import React, { useState } from 'react';
import './ExpandableSection.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

function ExpandableSection({id, icon, heading, subheading, children}) {
  const [isHovered, setIsHovered] = useState(false);

  function showHoveredContents(e) {
    console.log("mouse enter", e)
    setIsHovered(true);
  }

  function showHeading(e) {
    console.log("mouse out", e)
    setIsHovered(false);
  }

  function scroll(e) {
    if (e.key === "ArrowDown") {
      e.target.scrollBy({ top: -30});
    }
  }

  return (
    <>
      <div
        className='hoverable-container'
        tabIndex='0'
        role='presentation'
        onFocus={showHoveredContents}
        onMouseEnter={showHoveredContents}
        onBlur={showHeading}
        onMouseLeave={showHeading}
        onKeyDown={scroll}
      >
        { !isHovered ?
        <>
          <FontAwesomeIcon icon={icon} className='icon' />
          <h2>{heading}</h2>
        </> :
        <>
          <div className='hovered-contents'>
            <h2>{heading}</h2>
            <p>
              {children}
            </p>
          </div>
        </>
        }
      </div>
    </>
  );


  // const [isOpen, setIsOpen] = useState(false);

  // return (
  //     <div className={`expandable-container ${isOpen ? '' : 'closed'}`}>
  //       <FontAwesomeIcon icon={icon} className='icon' />
