  //       <button
  //         className='expandable-header'
  //         type='button'
  //         aria-expanded={isOpen}
  //         aria-controls={id}
  //         onClick={()=> setIsOpen(!isOpen)}
  //       >
  //         <span>{ subheading }</span>
  //         <span>{ isOpen ? '-' : '+' }</span>
  //       </button>
  //       {
  //         <div id={id} className='expandable-contents'>
  //           <p>
  //               {children}
  //           </p>
  //         </div>
  //       }
  //     </div>
  // );
}

export default ExpandableSection;
